$(function(){var n=$("#loginCarousel");n.carousel({interval:!1,keyboard:!1});$("#lnkToResetPassword").off("click").on("click",function(t){t.preventDefault();n.carousel("next")});$("#lnkToLogin").off("click").on("click",function(t){t.preventDefault();n.carousel("prev")});document.location.search.indexOf("resetPasswordEmailSent=True")>-1&&$.notify({icon:"fas fa-check",message:"Password reset email sent"},{type:"success",delay:5e3,animate:{enter:"animated fadeInDown",exit:"animated fadeOutUp"}});document.location.search.indexOf("?passwordReseted=True")>-1&&$.notify({icon:"fas fa-check",message:"Password successfully updated"},{type:"success",delay:5e3,animate:{enter:"animated fadeInDown",exit:"animated fadeOutUp"}})});