$(function(){var n=$("#fileProfileavatar");$(".fa-info-circle[data-toggle='tooltip']").tooltip();$(".applySelectTwo").select2({theme:"bootstrap"});$("#profile-img").off().on("click",function(){n.trigger("click")});n.off("change").on("change",function(n){var t=new FormData;t.append("avatar",n.target.files[0]);$.ajax({url:"../internal-api/profile/avatar",data:t,processData:!1,contentType:!1,type:"POST",success:function(n){console.log(n);$("#profile-img").attr("src",n);$("#txtEncodedAvatar").val(n)},error:function(n){console.log(n)}})})});